import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { ArrowLeft } from "lucide-react";
import { useAuth } from "@/hooks/useAuth";
import { useWebSocket } from "@/hooks/useWebSocket";
import { useToast } from "@/hooks/use-toast";
import { isUnauthorizedError } from "@/lib/authUtils";
import { apiRequest } from "@/lib/queryClient";
import { formatDate } from "@/lib/utils";

export default function Messages() {
  const [selectedThread, setSelectedThread] = useState<any>(null);
  const [messageText, setMessageText] = useState("");
  const { user } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: threads = [], isLoading: threadsLoading } = useQuery({
    queryKey: ["/api/messages/threads"],
  });

  const { data: messages = [], isLoading: messagesLoading } = useQuery({
    queryKey: ["/api/messages/threads", selectedThread?.id],
    enabled: !!selectedThread?.id,
  });

  // WebSocket for real-time messages
  useWebSocket({
    onMessage: (data) => {
      if (data.type === "new_message") {
        queryClient.invalidateQueries({ queryKey: ["/api/messages/threads"] });
        if (selectedThread?.id === data.threadId) {
          queryClient.invalidateQueries({ 
            queryKey: ["/api/messages/threads", selectedThread.id] 
          });
        }
      }
    },
  });

  const sendMessageMutation = useMutation({
    mutationFn: async (content: string) => {
      return apiRequest("POST", "/api/messages", {
        threadId: selectedThread.id,
        content,
      });
    },
    onSuccess: () => {
      setMessageText("");
      queryClient.invalidateQueries({ 
        queryKey: ["/api/messages/threads", selectedThread.id] 
      });
      queryClient.invalidateQueries({ queryKey: ["/api/messages/threads"] });
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to send message. Please try again.",
        variant: "destructive",
      });
    },
  });

  const handleSendMessage = () => {
    if (!messageText.trim()) return;
    sendMessageMutation.mutate(messageText.trim());
  };

  if (threadsLoading) {
    return (
      <div className="space-y-4">
        <div className="flex justify-between items-center">
          <div className="h-6 bg-muted rounded w-32 animate-pulse"></div>
          <div className="h-8 bg-muted rounded w-16 animate-pulse"></div>
        </div>
        {[...Array(3)].map((_, i) => (
          <div key={i} className="bg-card border border-border rounded-xl p-2.5 animate-pulse">
            <div className="flex gap-2.5">
              <div className="w-9 h-9 bg-muted rounded-full"></div>
              <div className="flex-1 space-y-2">
                <div className="h-4 bg-muted rounded w-24"></div>
                <div className="h-3 bg-muted rounded w-32"></div>
              </div>
            </div>
          </div>
        ))}
      </div>
    );
  }

  if (selectedThread) {
    return (
      <div className="space-y-4">
        {/* Chat Header */}
        <Card className="theme-transition">
          <CardContent className="p-3.5 flex items-center gap-2.5">
            <Button
              variant="ghost"
              size="sm"
              onClick={() => setSelectedThread(null)}
              data-testid="button-back-to-threads"
            >
              <ArrowLeft className="h-4 w-4" />
            </Button>
            <div className="w-8 h-8 rounded-full bg-secondary flex items-center justify-center font-bold text-sm">
              {selectedThread.otherUser.firstName?.[0] || selectedThread.otherUser.handle?.[1] || "U"}
            </div>
            <div className="font-semibold text-sm" data-testid="text-chat-user-name">
              {selectedThread.otherUser.firstName || selectedThread.otherUser.handle}
            </div>
          </CardContent>
        </Card>

        {/* Messages */}
        <Card className="theme-transition">
          <CardContent className="p-3.5">
            <div className="space-y-2 mb-4 max-h-64 overflow-y-auto">
              {messagesLoading ? (
                <div className="text-center py-4">Loading messages...</div>
              ) : messages.length === 0 ? (
                <div className="text-center py-8 text-muted-foreground">
                  <div className="text-2xl mb-2">ðŸ’¬</div>
                  <div className="text-sm">No messages yet</div>
                  <div className="text-xs">Start the conversation!</div>
                </div>
              ) : (
                messages.map((message: any) => {
                  const isMe = message.senderId === user?.id;
                  return (
                    <div 
                      key={message.id}
                      className={`max-w-4/5 ${isMe ? "ml-auto" : ""}`}
                      data-testid={`message-${message.id}`}
                    >
                      <div className={`p-2.5 rounded-xl text-sm ${
                        isMe 
                          ? "bg-secondary text-right" 
                          : "bg-accent"
                      }`}>
                        {message.content}
                      </div>
                      <div className={`text-xs text-muted-foreground mt-1 ${
                        isMe ? "text-right" : ""
                      }`}>
                        {formatDate(message.createdAt)}
                      </div>
                    </div>
                  );
                })
              )}
            </div>

            {/* Message Input */}
            <div className="flex gap-2">
              <Input 
                value={messageText}
                onChange={(e) => setMessageText(e.target.value)}
                placeholder="Type a message..."
                onKeyPress={(e) => e.key === "Enter" && handleSendMessage()}
                data-testid="input-message"
              />
              <Button 
                onClick={handleSendMessage}
                disabled={!messageText.trim() || sendMessageMutation.isPending}
                className="bg-pocket-red hover:bg-pocket-red-dark text-white"
                data-testid="button-send-message"
              >
                Send
              </Button>
            </div>
          </CardContent>
        </Card>
      </div>
    );
  }

  return (
    <div className="space-y-4">
      {/* Header */}
      <div className="flex justify-between items-center">
        <h2 className="text-lg font-bold">Messages</h2>
        <Button 
          className="bg-pocket-red hover:bg-pocket-red-dark text-white"
          size="sm"
          data-testid="button-new-message"
        >
          + New
        </Button>
      </div>

      {/* Threads List */}
      <div className="space-y-2.5">
        {threads.length === 0 ? (
          <Card className="theme-transition">
            <CardContent className="p-8 text-center">
              <div className="text-4xl mb-4">ðŸ’¬</div>
              <h3 className="text-lg font-semibold mb-3">Let's start connecting!</h3>
              <div className="max-w-md mx-auto space-y-4">
                <p className="text-muted-foreground">
                  Your conversations will appear here. Get the ball rolling!
                </p>
                <div className="bg-muted/30 rounded-lg p-4 text-left">
                  <h4 className="font-medium text-sm mb-2">ðŸ’¡ Ways to start chatting:</h4>
                  <ul className="text-xs text-muted-foreground space-y-1">
                    <li>â€¢ Jump on bounties that sound fun to you</li>
                    <li>â€¢ Connect with fellow weirdos in Friends</li>
                    <li>â€¢ Ask "wait, what exactly do you need?" ðŸ˜…</li>
                    <li>â€¢ Share your random talents with bounty posters</li>
                  </ul>
                </div>
                <p className="text-xs text-muted-foreground">
                  ðŸ’« Tip: Be yourself - personality pays here!
                </p>
              </div>
            </CardContent>
          </Card>
        ) : (
          threads.map((thread: any) => (
            <Card 
              key={thread.id} 
              className="cursor-pointer hover:bg-accent/50 theme-transition"
              onClick={() => setSelectedThread(thread)}
              data-testid={`thread-${thread.id}`}
            >
              <CardContent className="p-2.5">
                <div className="flex gap-2.5 items-center">
                  <div className="w-9 h-9 rounded-full bg-secondary flex items-center justify-center font-bold text-sm">
                    {thread.otherUser.firstName?.[0] || thread.otherUser.handle?.[1] || "U"}
                  </div>
                  <div className="flex-1 min-w-0">
                    <div className="font-semibold text-sm" data-testid="text-thread-user-name">
                      {thread.otherUser.firstName || thread.otherUser.handle}
                    </div>
                    <div className="text-xs text-muted-foreground truncate" data-testid="text-thread-last-message">
                      {thread.lastMessage?.content || "No messages yet"}
                    </div>
                  </div>
                  <div className="text-xs text-muted-foreground">
                    {thread.lastMessage && formatDate(thread.lastMessage.createdAt)}
                  </div>
                </div>
              </CardContent>
            </Card>
          ))
        )}
      </div>
    </div>
  );
}

client/src/pages/Post.tsx.content:
import { useState } from "react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Label } from "@/components/ui/label";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { useToast } from "@/hooks/use-toast";
import { useAuth } from "@/hooks/useAuth";
import { isUnauthorizedError } from "@/lib/authUtils";
import { apiRequest } from "@/lib/queryClient";
import { navigateToLogin } from "@/lib/navigation";

const postBountySchema = z.object({
  title: z.string().min(5, "Title must be at least 5 characters").max(255, "Title too long"),
  description: z.string().min(20, "Description must be at least 20 characters"),
  category: z.string().min(1, "Please select a category"),
  reward: z.string().refine((val) => parseFloat(val) >= 5, "Minimum reward is $5"),
  tags: z.string().optional(),
  duration: z.string().min(1, "Please select a duration"),
});

type PostBountyForm = z.infer<typeof postBountySchema>;

export default function Post() {
  const { toast } = useToast();
  const { user, isLoading } = useAuth();
  const queryClient = useQueryClient();

  // Show login prompt if not authenticated
  if (!isLoading && !user) {
    return (
      <div className="text-center space-y-4 mt-8">
        <div className="text-6xl">ðŸ”’</div>
        <h2 className="text-lg font-bold">Please Log In</h2>
        <p className="text-muted-foreground">You need to be logged in to post bounties.</p>
        <Button 
          onClick={() => navigateToLogin()}
          className="bg-pocket-red hover:bg-pocket-red-dark"
        >
          Log In to Post Bounties
        </Button>
      </div>
    );
  }

  if (isLoading) {
    return (
      <div className="text-center space-y-4 mt-8">
        <div className="text-4xl">â³</div>
        <p className="text-muted-foreground">Loading...</p>
      </div>
    );
  }

  const form = useForm<PostBountyForm>({
    resolver: zodResolver(postBountySchema),
    defaultValues: {
      title: "",
      description: "",
      category: "",
      reward: "5",
      tags: "",
      duration: "7",
    },
  });

  const postMutation = useMutation({
    mutationFn: async (data: PostBountyForm) => {
      const tags = data.tags ? data.tags.split(",").map(tag => tag.trim()).filter(Boolean) : [];
      return apiRequest("POST", "/api/bounties", {
        ...data,
        reward: parseFloat(data.reward),
        duration: parseInt(data.duration),
        tags,
      });
    },
    onSuccess: () => {
      toast({
        title: "Bounty Posted!",
        description: "Your bounty has been posted successfully.",
      });
      form.reset();
      queryClient.invalidateQueries({ queryKey: ["/api/bounties"] });
      queryClient.invalidateQueries({ queryKey: ["/api/auth/user"] });
    },
    onError: (error: any) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          navigateToLogin();
        }, 500);
        return;
      }

      // Parse error message from backend
      let errorMessage = "Failed to post bounty. Please try again.";
      if (error && typeof error === 'object' && 'message' in error) {
        errorMessage = error.message;
      } else if (error && error.response && error.response.data && error.response.data.message) {
        errorMessage = error.response.data.message;
      }

      toast({
        title: "Error",
        description: errorMessage,
        variant: "destructive",
      });
    },
  });

  const onSubmit = (data: PostBountyForm) => {
    const reward = parseFloat(data.reward);

    if ((user?.points || 0) < 5) {
      toast({
        title: "Insufficient Points",
        description: "You need at least 5 points to post a bounty.",
        variant: "destructive",
      });
      return;
    }

    if ((parseFloat(user?.balance || "0")) < reward) {
      toast({
        title: "Insufficient Balance",
        description: `You need $${reward.toFixed(2)} in your account balance to post this bounty. Your current balance: $${parseFloat(user?.balance || "0").toFixed(2)}`,
        variant: "destructive",
      });
      return;
    }

    postMutation.mutate(data);
  };

  return (
    <div className="space-y-4">
      <h2 className="text-lg font-bold">Post a New Bounty</h2>

      <Card className="theme-transition">
        <CardContent className="p-3.5">
          <Form {...form}>
            <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
              <FormField
                control={form.control}
                name="title"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel className="text-xs text-muted-foreground">
                      Bounty Title
                    </FormLabel>
                    <FormControl>
                      <Input 
                        placeholder="What do you need done?" 
                        {...field}
                        data-testid="input-bounty-title"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="description"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel className="text-xs text-muted-foreground">
                      Description
                    </FormLabel>
                    <FormControl>
                      <Textarea 
                        placeholder="Describe the task in detail..." 
                        rows={4}
                        className="resize-none"
                        {...field}
                        data-testid="textarea-bounty-description"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <div className="grid grid-cols-2 gap-3">
                <FormField
                  control={form.control}
                  name="category"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel className="text-xs text-muted-foreground">
                        Value Range
                      </FormLabel>
                      <Select onValueChange={field.onChange} value={field.value}>
                        <FormControl>
                          <SelectTrigger data-testid="select-bounty-category">
                            <SelectValue placeholder="Select value range" />
                          </SelectTrigger>
                        </FormControl>
                        <SelectContent>
                          <SelectItem value="quick_cash">ðŸ’µ Quick Cash ($5-15)</SelectItem>
                          <SelectItem value="good_money">ðŸ’° Good Money ($16-50)</SelectItem>
                          <SelectItem value="big_bucks">ðŸ’Ž Big Bucks ($51-100)</SelectItem>
                          <SelectItem value="major_bag">ðŸ† Major Bag ($100+)</SelectItem>
                          <SelectItem value="other">ðŸŽ² Other</SelectItem>
                        </SelectContent>
                      </Select>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="reward"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel className="text-xs text-muted-foreground">
                        Reward Amount ($)
                      </FormLabel>
                      <FormControl>
                        <Input 
                          type="tel"
                          pattern="[0-9]*\.?[0-9]*"
                          placeholder="5.00"
                          {...field}
                          data-testid="input-bounty-reward"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
              </div>

              <FormField
                control={form.control}
                name="tags"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel className="text-xs text-muted-foreground">
                      Tags (comma separated)
                    </FormLabel>
                    <FormControl>
                      <Input 
                        placeholder="mobile, testing, feedback" 
                        {...field}
                        data-testid="input-bounty-tags"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="duration"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel className="text-xs text-muted-foreground">
                      Duration
                    </FormLabel>
                    <Select onValueChange={field.onChange} value={field.value}>
                      <FormControl>
                        <SelectTrigger data-testid="select-bounty-duration">
                          <SelectValue placeholder="Select duration" />
                        </SelectTrigger>
                      </FormControl>
                      <SelectContent>
                        <SelectItem value="1">1 day</SelectItem>
                        <SelectItem value="3">3 days</SelectItem>
                        <SelectItem value="7">1 week</SelectItem>
                        <SelectItem value="14">2 weeks</SelectItem>
                        <SelectItem value="30">1 month</SelectItem>
                      </SelectContent>
                    </Select>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <div className="space-y-2">
                <Button 
                  type="submit" 
                  className="w-full bg-pocket-red hover:bg-pocket-red-dark text-white"
                  disabled={postMutation.isPending || (user?.points || 0) < 5 || (parseFloat(user?.balance || "0")) < parseFloat(form.watch("reward") || "5")}
                  data-testid="button-post-bounty"
                >
                  {postMutation.isPending ? "Posting..." : "Post Bounty"}
                </Button>
                <div className="text-xs text-muted-foreground text-center">
                  ðŸ’° Full amount held in escrow. Auto-refunds in 3 days minus {parseFloat(form.watch("reward") || "5") >= 250 ? "3.5%" : "5%"} fee if unclaimed.
                </div>
                {(user?.points || 0) < 5 && (
                  <div className="text-xs text-destructive text-center">
                    You need at least 5 points to post a bounty
                  </div>
                )}
                {(parseFloat(user?.balance || "0")) < parseFloat(form.watch("reward") || "5") && (
                  <div className="text-xs text-destructive text-center">
                    Insufficient balance: Need ${parseFloat(form.watch("reward") || "5").toFixed(2)}, have ${parseFloat(user?.balance || "0").toFixed(2)}
                  </div>
                )}
              </div>
            </form>
          </Form>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/Profile.tsx.content:
import { useState } from "react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Badge } from "@/components/ui/badge";
import { User, Star, Trophy, Clock, Edit2, Save, X, Camera } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { useAuth } from "@/hooks/useAuth";
import { isUnauthorizedError } from "@/lib/authUtils";
import { apiRequest } from "@/lib/queryClient";
import { navigateToLogin } from "@/lib/navigation";

export default function Profile() {
  const { user } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const [isEditing, setIsEditing] = useState(false);
  const [formData, setFormData] = useState({
    firstName: user?.firstName || "",
    lastName: user?.lastName || "",
    handle: user?.handle || "",
    bio: user?.bio || "",
    skills: user?.skills || "",
    experience: user?.experience || "",
  });

  const updateProfileMutation = useMutation({
    mutationFn: async (data: any) => {
      return apiRequest("PATCH", "/api/user/profile", data);
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Profile updated successfully!",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/auth/user"] });
      setIsEditing(false);
    },
    onError: (error: any) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          navigateToLogin();
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to update profile. Please try again.",
        variant: "destructive",
      });
    },
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    updateProfileMutation.mutate(formData);
  };

  const handleCancel = () => {
    setFormData({
      firstName: user?.firstName || "",
      lastName: user?.lastName || "",
      handle: user?.handle || "",
      bio: user?.bio || "",
      skills: user?.skills || "",
      experience: user?.experience || "",
    });
    setIsEditing(false);
  };

  const handleInputChange = (field: string, value: string) => {
    setFormData(prev => ({ ...prev, [field]: value }));
  };

  const getInitials = () => {
    if (formData.firstName && formData.lastName) {
      return `${formData.firstName[0]}${formData.lastName[0]}`.toUpperCase();
    }
    if (formData.handle) {
      return formData.handle[0].toUpperCase();
    }
    return user?.email?.[0]?.toUpperCase() || "U";
  };

  const getDisplayName = () => {
    if (formData.firstName && formData.lastName) {
      return `${formData.firstName} ${formData.lastName}`;
    }
    return formData.handle || user?.email || "Unknown User";
  };

  if (!user) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin w-8 h-8 border-4 border-primary border-t-transparent rounded-full" />
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <h1 className="text-2xl font-bold">My Profile</h1>
        {!isEditing ? (
          <Button 
            onClick={() => setIsEditing(true)}
            className="flex items-center gap-2"
            data-testid="button-edit-profile"
          >
            <Edit2 className="h-4 w-4" />
            Edit Profile
          </Button>
        ) : (
          <div className="flex gap-2">
            <Button 
              onClick={handleCancel}
              variant="outline"
              className="flex items-center gap-2"
              data-testid="button-cancel-edit"
            >
              <X className="h-4 w-4" />
              Cancel
            </Button>
            <Button 
              onClick={handleSubmit}
              disabled={updateProfileMutation.isPending}
              className="flex items-center gap-2"
              data-testid="button-save-profile"
            >
              <Save className="h-4 w-4" />
              {updateProfileMutation.isPending ? "Saving..." : "Save"}
            </Button>
          </div>
        )}
      </div>

      {/* Profile Header */}
      <Card className="theme-transition">
        <CardContent className="p-6">
          <div className="flex items-center gap-6">
            {/* Profile Picture */}
            <div className="relative">
              <div className="w-24 h-24 rounded-full bg-primary/10 flex items-center justify-center text-3xl font-bold border-4 border-border">
                {user?.profileImageUrl ? (
                  <img 
                    src={user.profileImageUrl} 
                    alt="Profile" 
                    className="w-full h-full rounded-full object-cover"
                    data-testid="img-profile-photo"
                  />
                ) : (
                  <span data-testid="text-profile-initials">
                    {getInitials()}
                  </span>
                )}
              </div>
              {isEditing && (
                <Button
                  size="sm"
                  className="absolute -bottom-2 -right-2 rounded-full w-8 h-8 p-0"
                  data-testid="button-change-photo"
                  onClick={() => {
                    toast({
                      title: "Coming Soon",
                      description: "Profile photo upload will be available soon!",
                    });
                  }}
                >
                  <Camera className="h-3 w-3" />
                </Button>
              )}
            </div>

            {/* Profile Info */}
            <div className="flex-1">
              {isEditing ? (
                <div className="space-y-3">
                  <div className="grid grid-cols-2 gap-3">
                    <div>
                      <Label htmlFor="firstName" className="text-sm">First Name</Label>
                      <Input
                        id="firstName"
                        value={formData.firstName}
                        onChange={(e) => handleInputChange("firstName", e.target.value)}
                        placeholder="Enter first name"
                        data-testid="input-first-name"
                      />
                    </div>
                    <div>
                      <Label htmlFor="lastName" className="text-sm">Last Name</Label>
                      <Input
                        id="lastName"
                        value={formData.lastName}
                        onChange={(e) => handleInputChange("lastName", e.target.value)}
                        placeholder="Enter last name"
                        data-testid="input-last-name"
                      />
                    </div>
                  </div>
                  <div>
                    <Label htmlFor="handle" className="text-sm">Username/Handle</Label>
                    <Input
                      id="handle"
                      value={formData.handle}
                      onChange={(e) => handleInputChange("handle", e.target.value)}
                      placeholder="@username"
                      data-testid="input-handle"
                    />
                  </div>
                </div>
              ) : (
                <>
                  <h2 className="text-2xl font-bold mb-2" data-testid="text-display-name">
                    {getDisplayName()}
                  </h2>
                  <p className="text-muted-foreground mb-1" data-testid="text-email">
                    {user.email}
                  </p>
                  <p className="text-muted-foreground mb-3" data-testid="text-handle">
                    {formData.handle || "@user"}
                  </p>
                </>
              )}

              {/* User Stats */}
              <div className="flex items-center gap-4 mt-3">
                <Badge variant="secondary" className="flex items-center gap-1">
                  <Trophy className="h-3 w-3" />
                  Level {user.level || 1}
                </Badge>
                <Badge variant="secondary" className="flex items-center gap-1">
                  <Star className="h-3 w-3" />
                  {user.rating || "0.0"} ({user.reviewCount || 0} reviews)
                </Badge>
                <Badge variant="secondary" className="flex items-center gap-1">
                  <Clock className="h-3 w-3" />
                  Joined {new Date(user.createdAt).toLocaleDateString()}
                </Badge>
              </div>
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Bio Section */}
      <Card className="theme-transition">
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <User className="h-5 w-5" />
            About Me
          </CardTitle>
        </CardHeader>
        <CardContent>
          {isEditing ? (
            <div>
              <Label htmlFor="bio" className="text-sm">Bio</Label>
              <Textarea
                id="bio"
                value={formData.bio}
                onChange={(e) => handleInputChange("bio", e.target.value)}
                placeholder="Tell us about yourself..."
                rows={4}
                className="mt-1"
                data-testid="textarea-bio"
              />
              <p className="text-xs text-muted-foreground mt-1">
                Share your background, interests, and what makes you unique
              </p>
            </div>
          ) : (
            <p className="text-muted-foreground" data-testid="text-bio">
              {formData.bio || "No bio added yet. Click Edit Profile to add one!"}
            </p>
          )}
        </CardContent>
      </Card>

      {/* Skills & Experience */}
      <Card className="theme-transition">
        <CardHeader>
          <CardTitle>Skills & Experience</CardTitle>
        </CardHeader>
        <CardContent className="space-y-4">
          {isEditing ? (
            <>
              <div>
                <Label htmlFor="skills" className="text-sm">Skills</Label>
                <Textarea
                  id="skills"
                  value={formData.skills}
                  onChange={(e) => handleInputChange("skills", e.target.value)}
                  placeholder="e.g., JavaScript, Design, Writing, Marketing, Photography"
                  rows={3}
                  className="mt-1"
                  data-testid="textarea-skills"
                />
                <p className="text-xs text-muted-foreground mt-1">
                  List your skills separated by commas
                </p>
              </div>
              <div>
                <Label htmlFor="experience" className="text-sm">Experience Level</Label>
                <Input
                  id="experience"
                  value={formData.experience}
                  onChange={(e) => handleInputChange("experience", e.target.value)}
                  placeholder="e.g., Beginner, Intermediate, Expert, 5+ years"
                  className="mt-1"
                  data-testid="input-experience"
                />
              </div>
            </>
          ) : (
            <>
              <div>
                <h4 className="font-semibold mb-2">Skills</h4>
                {formData.skills ? (
                  <div className="flex flex-wrap gap-2">
                    {formData.skills.split(",").map((skill: string, index: number) => (
                      <Badge key={index} variant="secondary" className="bg-blue-900/30 border-blue-600/50 text-blue-300">
                        {skill.trim()}
                      </Badge>
                    ))}
                  </div>
                ) : (
                  <p className="text-muted-foreground text-sm">No skills added yet</p>
                )}
              </div>
              <div>
                <h4 className="font-semibold mb-2">Experience Level</h4>
                <p className="text-muted-foreground" data-testid="text-experience">
                  {formData.experience || "Not specified"}
                </p>
              </div>
            </>
          )}
        </CardContent>
      </Card>

      {/* Stats Overview */}
      <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
        <Card className="theme-transition">
          <CardContent className="p-4 text-center">
            <div className="text-2xl font-bold text-pocket-gold">
              ${parseFloat(user.balance || "0").toFixed(2)}
            </div>
            <div className="text-xs text-muted-foreground">Current Balance</div>
          </CardContent>
        </Card>

        <Card className="theme-transition">
          <CardContent className="p-4 text-center">
            <div className="text-2xl font-bold text-green-600">
              ${parseFloat(user.lifetimeEarned || "0").toFixed(2)}
            </div>
            <div className="text-xs text-muted-foreground">Lifetime Earned</div>
          </CardContent>
        </Card>

        <Card className="theme-transition">
          <CardContent className="p-4 text-center">
            <div className="text-2xl font-bold text-blue-600">
              {user.points || 0}
            </div>
            <div className="text-xs text-muted-foreground">Points</div>
          </CardContent>
        </Card>

        <Card className="theme-transition">
          <CardContent className="p-4 text-center">
            <div className="text-2xl font-bold text-purple-600">
              {user.reviewCount || 0}
            </div>
            <div className="text-xs text-muted-foreground">Reviews</div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

client/src/pages/Settings.tsx.content:
import { useState } from "react";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Switch } from "@/components/ui/switch";
import { Label } from "@/components/ui/label";
import { useTheme } from "@/components/ThemeProvider";
import { useToast } from "@/hooks/use-toast";
import Tutorial from "@/components/Tutorial";
import { PlayCircle, HelpCircle } from "lucide-react";

export default function Settings() {
  const { theme, toggleTheme } = useTheme();
  const { toast } = useToast();
  const [showTutorial, setShowTutorial] = useState(false);

  // Local state for notification preferences
  const [notifications, setNotifications] = useState({
    newBounties: true,
    messages: true,
    payments: true,
    friendRequests: true,
  });

  // Local state for privacy settings
  const [privacy, setPrivacy] = useState({
    showOnlineStatus: true,
    publicProfile: true,
    showEarnings: false,
  });

  const handleLogout = () => {
    window.location.href = "/api/logout";
  };

  const handleEditProfile = () => {
    window.location.href = "/profile";
  };

  const handlePaymentMethods = () => {
    window.location.href = "/account";
  };

  const handlePrivacyPolicy = () => {
    toast({
      title: "Privacy Policy",
      description: "Please contact support for privacy policy information.",
    });
  };

  const handleTermsOfService = () => {
    toast({
      title: "Terms of Service",
      description: "Please contact support for terms of service information.",
    });
  };

  const handleNotificationChange = (key: keyof typeof notifications) => {
    setNotifications(prev => ({
      ...prev,
      [key]: !prev[key]
    }));
    toast({
      title: "Settings Updated",
      description: "Your notification preferences have been saved.",
    });
  };

  const handlePrivacyChange = (key: keyof typeof privacy) => {
    setPrivacy(prev => ({
      ...prev,
      [key]: !prev[key]
    }));
    toast({
      title: "Settings Updated",
      description: "Your privacy settings have been saved.",
    });
  };

  const handleTutorialReplay = () => {
    setShowTutorial(true);
  };

  const handleTutorialClose = () => {
    setShowTutorial(false);
  };

  const handleGetHelp = () => {
    toast({
      title: "Need Help? ðŸ¤”",
      description: "Try the tutorial or contact support for assistance!",
    });
  };

  return (
    <div className="space-y-4">
      <h2 className="text-lg font-bold">Settings</h2>

      {/* Appearance */}
      <Card className="theme-transition">
        <CardContent className="p-3.5">
          <h3 className="text-sm font-semibold mb-3">Appearance</h3>
          <div className="flex justify-between items-center">
            <div>
              <Label className="text-sm">Dark Mode</Label>
              <div className="text-xs text-muted-foreground">
                Switch between light and dark themes
              </div>
            </div>
            <Switch
              checked={theme === "dark"}
              onCheckedChange={toggleTheme}
              data-testid="switch-dark-mode"
            />
          </div>
        </CardContent>
      </Card>

      {/* Notifications */}
      <Card className="theme-transition">
        <CardContent className="p-3.5">
          <h3 className="text-sm font-semibold mb-3">Notifications</h3>
          <div className="space-y-3">
            <div className="flex justify-between items-center">
              <Label className="text-sm">New bounties</Label>
              <Switch
                checked={notifications.newBounties}
                onCheckedChange={() => handleNotificationChange("newBounties")}
                data-testid="switch-new-bounties"
              />
            </div>

            <div className="flex justify-between items-center">
              <Label className="text-sm">Messages</Label>
              <Switch
                checked={notifications.messages}
                onCheckedChange={() => handleNotificationChange("messages")}
                data-testid="switch-messages"
              />
            </div>

            <div className="flex justify-between items-center">
              <Label className="text-sm">Payment updates</Label>
              <Switch
                checked={notifications.payments}
                onCheckedChange={() => handleNotificationChange("payments")}
                data-testid="switch-payments"
              />
            </div>

            <div className="flex justify-between items-center">
              <Label className="text-sm">Friend requests</Label>
              <Switch
                checked={notifications.friendRequests}
                onCheckedChange={() => handleNotificationChange("friendRequests")}
                data-testid="switch-friend-requests"
              />
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Privacy */}
      <Card className="theme-transition">
        <CardContent className="p-3.5">
          <h3 className="text-sm font-semibold mb-3">Privacy</h3>
          <div className="space-y-3">
            <div className="flex justify-between items-center">
              <Label className="text-sm">Show online status</Label>
              <Switch
                checked={privacy.showOnlineStatus}
                onCheckedChange={() => handlePrivacyChange("showOnlineStatus")}
                data-testid="switch-online-status"
              />
            </div>

            <div className="flex justify-between items-center">
              <Label className="text-sm">Public profile</Label>
              <Switch
                checked={privacy.publicProfile}
                onCheckedChange={() => handlePrivacyChange("publicProfile")}
                data-testid="switch-public-profile"
              />
            </div>

            <div className="flex justify-between items-center">
              <Label className="text-sm">Show earnings</Label>
              <Switch
                checked={privacy.showEarnings}
                onCheckedChange={() => handlePrivacyChange("showEarnings")}
                data-testid="switch-show-earnings"
              />
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Help & Support */}
      <Card className="theme-transition">
        <CardContent className="p-3.5">
          <h3 className="text-sm font-semibold mb-3">Help & Support</h3>
          <div className="space-y-2">
            <Button
              variant="ghost"
              onClick={handleTutorialReplay}
              className="w-full justify-start text-left p-2 text-sm hover:bg-accent"
              data-testid="button-replay-tutorial"
            >
              <PlayCircle className="h-4 w-4 mr-2" />
              Replay Interactive Tutorial
            </Button>

            <Button
              variant="ghost"
              onClick={handleGetHelp}
              className="w-full justify-start text-left p-2 text-sm hover:bg-accent"
              data-testid="button-get-help"
            >
              <HelpCircle className="h-4 w-4 mr-2" />
              Get Help & Support
            </Button>
          </div>
        </CardContent>
      </Card>

      {/* Account */}
      <Card className="theme-transition">
        <CardContent className="p-3.5">
          <h3 className="text-sm font-semibold mb-3">Account</h3>
          <div className="space-y-2">
            <Button
              variant="ghost"
              onClick={handleEditProfile}
              className="w-full justify-start text-left p-2 text-sm hover:bg-accent"
              data-testid="button-edit-profile"
            >
              Edit Profile
            </Button>

            <Button
              variant="ghost"
              onClick={handlePaymentMethods}
              className="w-full justify-start text-left p-2 text-sm hover:bg-accent"
              data-testid="button-payment-methods"
            >
              Payment Methods
            </Button>

            <Button
              variant="ghost"
              onClick={handlePrivacyPolicy}
              className="w-full justify-start text-left p-2 text-sm hover:bg-accent"
              data-testid="button-privacy-policy"
            >
              Privacy Policy
            </Button>

            <Button
              variant="ghost"
              onClick={handleTermsOfService}
              className="w-full justify-start text-left p-2 text-sm hover:bg-accent"
              data-testid="button-terms-of-service"
            >
              Terms of Service
            </Button>

            <Button
              variant="ghost"
              onClick={handleLogout}
              className="w-full justify-start text-left p-2 text-sm text-destructive hover:bg-destructive/10"
              data-testid="button-sign-out"
            >
              Sign Out
            </Button>
          </div>
        </CardContent>
      </Card>

      {/* Tutorial Modal */}
      {showTutorial && (
        <div className="fixed inset-0 bg-black/60 flex items-center justify-center p-4 z-[9999]">
          <div 
            className="bg-background rounded-lg shadow-xl max-w-md w-full max-h-[90vh] overflow-auto border border-border"
            onClick={(e) => e.stopPropagation()}
          >
            <Tutorial onClose={handleTutorialClose} />
          </div>
        </div>
      )}
    </div>
  );
}

