import { useState, useEffect } from "react";
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { useToast } from "@/hooks/use-toast";
import { isUnauthorizedError } from "@/lib/authUtils";
import { CreatorStatsModal } from "@/components/CreatorStatsModal";
import { 
  DollarSign, 
  TrendingUp, 
  Activity, 
  Users, 
  Target, 
  BarChart3,
  Calendar,
  Percent,
  ArrowUpRight,
  ArrowDownRight,
  Crown,
  CreditCard,
  Wallet,
  ShoppingCart,
  Gamepad2,
  Trophy,
  UserCheck,
  RefreshCw,
  Download
} from "lucide-react";

interface CreatorStats {
  revenue: {
    data: Array<{
      id: string;
      amount: string;
      source: string;
      description: string;
      createdAt: string;
    }>;
    total: string;
    transactionCount: number;
    avgPerTransaction: string;
  };
  users: {
    total: number;
    active: number;
    totalBalance: string;
    newLast30Days: number;
    growthRate: string;
  };
  bounties: {
    total: number;
    active: number;
    completed: number;
    totalValue: string;
    completionRate: string;
  };
  transactions: {
    total: number;
    totalVolume: string;
    deposits: number;
    withdrawals: number;
    avgTransactionSize: string;
  };
  spending: {
    totalUserSpent: string;
    pointPurchases: {
      total: string;
      count: number;
      avgPurchase: string;
    };
    withdrawals: {
      total: string;
      count: number;
      avgWithdrawal: string;
    };
    refunds: {
      total: string;
      count: number;
    };
    boosts?: {
      total: string;
      count: number;
    };
    breakdown: Record<string, number>;
    last30Days: {
      pointPurchases: string;
      spending: string;
    };
  };
  gameStats?: {
    totalGamesPlayed: number;
    totalPointsEarned: number;
    mostPopularGames: Array<{
      name: string;
      plays: number;
      pointsEarned: number;
    }>;
    recentGameActivity: Array<{
      game: string;
      points: number;
      userId: string;
      timestamp: string;
    }>;
  };
  topPerformers?: {
    topEarners: Array<{
      id: string;
      name: string;
      earned: string;
    }>;
    topSpenders: Array<{
      id: string;
      name: string;
      spent: string;
    }>;
    mostActive: Array<{
      id: string;
      name: string;
      actions: number;
    }>;
  };
  engagement?: {
    dailyActiveUsers: number;
    weeklyActiveUsers: number;
    monthlyActiveUsers: number;
    retentionRate: string;
    avgSessionLength: string;
    bounceRate: string;
  };
  activity: Array<{
    id: string;
    type: string;
    description: string;
    createdAt: string;
    userId: string;
  }>;
}

export default function CreatorDashboard() {
  const { toast } = useToast();
  const [modalOpen, setModalOpen] = useState(false);
  const [modalType, setModalType] = useState<'users' | 'revenue' | 'bounties' | 'points' | 'spending' | null>(null);
  const [modalTitle, setModalTitle] = useState("");

  const openDetailsModal = (type: 'users' | 'revenue' | 'bounties' | 'points' | 'spending', title: string) => {
    setModalType(type);
    setModalTitle(title);
    setModalOpen(true);
  };

  const { data: stats, isLoading, error } = useQuery<CreatorStats>({
    queryKey: ["/api/creator/stats"],
    retry: false,
  });

  // Handle errors
  useEffect(() => {
    if (error) {
      if (isUnauthorizedError(error as Error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }

      if (error.message.includes('403')) {
        toast({
          title: "Access Denied",
          description: "You don't have creator permissions to view these analytics",
          variant: "destructive",
        });
        return;
      }

      toast({
        title: "Error",
        description: "Failed to load creator analytics",
        variant: "destructive",
      });
    }
  }, [error, toast]);

  if (isLoading) {
    return (
      <div className="space-y-6">
        <h1 className="text-2xl font-bold flex items-center gap-2">
          <Crown className="h-6 w-6 text-yellow-500" />
          Creator Analytics
        </h1>
        <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
          {[1, 2, 3, 4].map((i) => (
            <Card key={i} className="theme-transition">
              <CardContent className="p-6">
                <div className="animate-pulse space-y-3">
                  <div className="h-4 bg-gray-200 dark:bg-gray-700 rounded w-1/2"></div>
                  <div className="h-8 bg-gray-200 dark:bg-gray-700 rounded w-3/4"></div>
                </div>
              </CardContent>
            </Card>
          ))}
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="space-y-6">
        <h1 className="text-2xl font-bold flex items-center gap-2">
          <Crown className="h-6 w-6 text-yellow-500" />
          Creator Analytics
        </h1>
        <Card className="theme-transition">
          <CardContent className="p-6 text-center">
            <div className="text-muted-foreground">
              <div className="text-6xl mb-4">ðŸš«</div>
              <h3 className="text-lg font-semibold mb-2">Access Restricted</h3>
              <p className="text-sm">You need creator permissions to view these analytics</p>
            </div>
          </CardContent>
        </Card>
      </div>
    );
  }

  if (!stats) return null;

  const getActivityIcon = (type: string) => {
    switch (type) {
      case 'bounty_posted': return 'ðŸ“‹';
      case 'bounty_applied': return 'âœ‹';
      case 'bounty_completed': return 'âœ…';
      case 'friend_added': return 'ðŸ‘¥';
      case 'payment_made': return 'ðŸ’³';
      default: return 'ðŸ“±';
    }
  };

  const formatGrowthRate = (rate: string) => {
    const numRate = parseFloat(rate);
    if (numRate > 0) {
      return (
        <div className="flex items-center gap-1 text-green-600">
          <ArrowUpRight className="h-4 w-4" />
          +{rate}%
        </div>
      );
    } else if (numRate < 0) {
      return (
        <div className="flex items-center gap-1 text-red-600">
          <ArrowDownRight className="h-4 w-4" />
          {rate}%
        </div>
      );
    }
    return <span className="text-muted-foreground">0%</span>;
  };

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <h1 className="text-2xl font-bold flex items-center gap-2">
          <Crown className="h-6 w-6 text-yellow-500" />
