import type { Request, Response, NextFunction } from "express";
import { z } from "zod";

export function validateRequest(schema: z.ZodSchema) {
  return async (req: Request, res: Response, next: NextFunction) => {
    try {
      const validated = await schema.parseAsync(req.body);
      req.body = validated;
      next();
    } catch (error) {
      if (error instanceof z.ZodError) {
        res.status(400).json({
          message: "Validation error",
          errors: error.errors.map(e => ({
            field: e.path.join("."),
            message: e.message
          }))
        });
      } else {
        res.status(500).json({ message: "Internal validation error" });
      }
    }
  };
}

// Common validation schemas
export const amountSchema = z.object({
  amount: z.string()
    .regex(/^\d+(\.\d{1,2})?$/, "Invalid amount format")
    .refine(val => parseFloat(val) > 0, "Amount must be positive")
    .refine(val => parseFloat(val) <= 10000, "Amount exceeds maximum limit")
});

export const bountyCreateSchema = z.object({
  title: z.string().min(5).max(255),
  description: z.string().min(20).max(5000),
  category: z.string().min(1).max(100),
  reward: z.string().regex(/^\d+(\.\d{1,2})?$/),
  duration: z.number().min(1).max(90),
  tags: z.array(z.string()).optional()
});

export const messageSchema = z.object({
  content: z.string().min(1).max(1000)
});

export const paginationSchema = z.object({
  page: z.number().min(1).default(1),
  limit: z.number().min(1).max(100).default(20),
  sortBy: z.string().optional(),
  order: z.enum(["asc", "desc"]).default("desc")
});

server/utils/cache.ts.content:
interface CacheEntry<T> {
  data: T;
  expiresAt: number;
}

export class MemoryCache<T = any> {
  private cache = new Map<string, CacheEntry<T>>();
  private defaultTTL: number;

  constructor(defaultTTL: number = 60000) { // 1 minute default
    this.defaultTTL = defaultTTL;

    // Clean up expired entries every minute
    setInterval(() => this.cleanup(), 60000);
  }

  set(key: string, value: T, ttl?: number): void {
    const expiresAt = Date.now() + (ttl || this.defaultTTL);
    this.cache.set(key, { data: value, expiresAt });
  }

  get(key: string): T | undefined {
    const entry = this.cache.get(key);

    if (!entry) return undefined;

    if (entry.expiresAt < Date.now()) {
      this.cache.delete(key);
      return undefined;
    }

    return entry.data;
  }

  has(key: string): boolean {
    return this.get(key) !== undefined;
  }

  delete(key: string): void {
    this.cache.delete(key);
  }

  clear(): void {
    this.cache.clear();
  }

  private cleanup(): void {
    const now = Date.now();
    this.cache.forEach((entry, key) => {
      if (entry.expiresAt < now) {
        this.cache.delete(key);
      }
    });
  }
}

// Specialized caches for different data types
export const userCache = new MemoryCache(5 * 60 * 1000); // 5 minutes
export const bountyCache = new MemoryCache(2 * 60 * 1000); // 2 minutes
export const activityCache = new MemoryCache(60 * 1000); // 1 minute

