import { useState, useEffect, useCallback, useRef } from "react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { useToast } from "@/hooks/use-toast";
import { useAuth } from "@/hooks/useAuth";
import { apiRequest } from "@/lib/queryClient";
import { Maximize, Minimize } from "lucide-react";

interface GameState {
  sequence: number[];
  playerSequence: number[];
  currentStep: number;
  showingSequence: boolean;
  score: number;
  gameStatus: "waiting" | "playing" | "gameover" | "showing";
  round: number;
  showingIndex: number;
}

const COLORS = [
  { id: 0, color: "bg-red-500", activeColor: "bg-red-300", sound: "C4" },
  { id: 1, color: "bg-blue-500", activeColor: "bg-blue-300", sound: "E4" },
  { id: 2, color: "bg-green-500", activeColor: "bg-green-300", sound: "G4" },
  { id: 3, color: "bg-yellow-500", activeColor: "bg-yellow-300", sound: "C5" },
];

export default function SimonSays() {
  const [gameState, setGameState] = useState<GameState>({
    sequence: [],
    playerSequence: [],
    currentStep: 0,
    showingSequence: false,
    score: 0,
    gameStatus: "waiting",
    round: 0,
    showingIndex: 0,
  });

  const [activeButtons, setActiveButtons] = useState<number[]>([]);
  const [bestScore, setBestScore] = useState(() => {
    return parseInt(localStorage.getItem("simon-says-best-score") || "0", 10);
  });
  const [isFullscreen, setIsFullscreen] = useState(false);
  const gameContainerRef = useRef<HTMLDivElement>(null);

  const { user } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const awardPointsMutation = useMutation({
    mutationFn: async (points: number) => {
      return apiRequest("POST", "/api/user/points", {
        points,
        reason: `Simon Says - reached round ${gameState.round}`,
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/auth/user"] });
      toast({
        title: "Points Earned!",
        description: `You earned ${Math.floor(gameState.score / 6)} points!`,
      });
    },
  });

  const playSound = useCallback((colorId: number) => {
    // Simple audio feedback using Web Audio API
    if (typeof window !== 'undefined' && 'AudioContext' in window) {
      const audioContext = new (window.AudioContext || (window as any).webkitAudioContext)();
      const oscillator = audioContext.createOscillator();
      const gainNode = audioContext.createGain();

      const frequencies = { 0: 261.63, 1: 329.63, 2: 392.00, 3: 523.25 }; // C4, E4, G4, C5

      oscillator.connect(gainNode);
      gainNode.connect(audioContext.destination);

      oscillator.frequency.value = frequencies[colorId as keyof typeof frequencies];
      oscillator.type = 'square';

      gainNode.gain.setValueAtTime(0, audioContext.currentTime);
      gainNode.gain.linearRampToValueAtTime(0.1, audioContext.currentTime + 0.01);
      gainNode.gain.exponentialRampToValueAtTime(0.001, audioContext.currentTime + 0.3);

      oscillator.start(audioContext.currentTime);
      oscillator.stop(audioContext.currentTime + 0.3);
    }
  }, []);

  const flashButton = useCallback((colorId: number, duration: number = 600) => {
    setActiveButtons(prev => [...prev, colorId]);
    playSound(colorId);

    setTimeout(() => {
      setActiveButtons(prev => prev.filter(id => id !== colorId));
    }, duration);
  }, [playSound]);

  const addToSequence = useCallback(() => {
    const newColor = Math.floor(Math.random() * 4);
    setGameState(prev => ({
      ...prev,
      sequence: [...prev.sequence, newColor],
      round: prev.round + 1,
      score: prev.score + prev.round * 10,
    }));
  }, []);

  const showSequence = useCallback(() => {
    setGameState(prev => ({ ...prev, gameStatus: "showing", showingIndex: 0 }));

    const showNext = (index: number) => {
      if (index >= gameState.sequence.length) {
        setGameState(prev => ({ 
          ...prev, 
          gameStatus: "playing", 
          playerSequence: [],
          currentStep: 0 
        }));
        return;
      }

      setTimeout(() => {
        flashButton(gameState.sequence[index]);
        showNext(index + 1);
      }, 800);
    };

    showNext(0);
  }, [gameState.sequence, flashButton]);

  const resetGame = useCallback(() => {
    setGameState({
      sequence: [],
      playerSequence: [],
      currentStep: 0,
      showingSequence: false,
      score: 0,
      gameStatus: "waiting",
      round: 0,
      showingIndex: 0,
    });
    setActiveButtons([]);
  }, []);

  const startGame = useCallback(() => {
    resetGame();
    setTimeout(() => {
      setGameState(prev => ({ ...prev, gameStatus: "playing" }));
      addToSequence();
    }, 100);
  }, [resetGame, addToSequence]);

  const toggleFullscreen = async () => {
    if (!gameContainerRef.current) return;

    try {
      if (!isFullscreen) {
        if (gameContainerRef.current.requestFullscreen) {
          await gameContainerRef.current.requestFullscreen();
        }
      } else {
        if (document.exitFullscreen) {
          await document.exitFullscreen();
        }
      }
    } catch (error) {
      console.error('Fullscreen error:', error);
    }
  };

  useEffect(() => {
    const handleFullscreenChange = () => {
      setIsFullscreen(!!document.fullscreenElement);
    };

    document.addEventListener('fullscreenchange', handleFullscreenChange);
    return () => document.removeEventListener('fullscreenchange', handleFullscreenChange);
  }, []);

  const handleButtonPress = useCallback((colorId: number) => {
    if (gameState.gameStatus !== "playing") return;

    flashButton(colorId, 200);

    setGameState(prev => {
      const newPlayerSequence = [...prev.playerSequence, colorId];
      const expectedColor = prev.sequence[prev.currentStep];

      if (colorId !== expectedColor) {
        // Wrong color - game over
        toast({
          title: "Wrong Color!",
          description: `You reached round ${prev.round}`,
          variant: "destructive",
        });
        return { ...prev, gameStatus: "gameover" };
      }

      const newCurrentStep = prev.currentStep + 1;

      if (newCurrentStep === prev.sequence.length) {
        // Completed the sequence - add new color and show again
        toast({
          title: `Round ${prev.round} Complete!`,
          description: "Watch the new sequence!",
        });

        setTimeout(() => {
          addToSequence();
          setTimeout(() => showSequence(), 500);
        }, 1000);

        return {
          ...prev,
          playerSequence: newPlayerSequence,
          currentStep: newCurrentStep,
          gameStatus: "showing",
        };
      }

      return {
        ...prev,
        playerSequence: newPlayerSequence,
        currentStep: newCurrentStep,
      };
    });
  }, [gameState.gameStatus, gameState.sequence, flashButton, toast, addToSequence, showSequence]);

  useEffect(() => {
    if (gameState.sequence.length > 0 && gameState.gameStatus === "playing" && gameState.currentStep === 0) {
      showSequence();
    }
  }, [gameState.sequence, gameState.gameStatus, gameState.currentStep, showSequence]);

  useEffect(() => {
    if (gameState.gameStatus === "gameover") {
      const newBestScore = Math.max(bestScore, gameState.score);
      if (newBestScore > bestScore) {
        setBestScore(newBestScore);
        localStorage.setItem("simon-says-best-score", newBestScore.toString());
      }

      const pointsEarned = Math.floor(gameState.score / 6);
      if (pointsEarned > 0) {
        awardPointsMutation.mutate(pointsEarned);
      }
    }
  }, [gameState.gameStatus, gameState.score, bestScore, awardPointsMutation]);

  return (
    <div ref={gameContainerRef} className={`space-y-4 ${isFullscreen ? 'fixed inset-0 z-50 bg-background p-4 overflow-auto' : ''}`}>
      <div className="text-center">
        <h2 className="text-lg font-bold mb-2">ðŸŽµ Simon Says</h2>
        <p className="text-sm text-muted-foreground">Repeat the color sequence to earn points!</p>
      </div>

      <Card className="theme-transition">
        <CardContent className="p-6">
          <div className="grid grid-cols-2 gap-4 max-w-md mx-auto">
            {COLORS.map((color) => (
              <button
                key={color.id}
                onClick={() => handleButtonPress(color.id)}
                onTouchStart={(e) => {
                  e.preventDefault();
                  if (gameState.gameStatus === "playing") {
                    handleButtonPress(color.id);
                  }
                }}
                disabled={gameState.gameStatus !== "playing"}
                className={`aspect-square rounded-2xl transition-all duration-200 border-4 border-gray-300 dark:border-gray-600 touch-none select-none ${
                  activeButtons.includes(color.id)
                    ? `${color.activeColor} scale-95 border-white`
                    : `${color.color} hover:scale-105 shadow-lg`
                } ${
                  gameState.gameStatus === "playing" ? "cursor-pointer" : "cursor-not-allowed opacity-75"
                }`}
                data-testid={`simon-button-${color.id}`}
              >
                <div className="w-full h-full flex items-center justify-center text-2xl font-bold text-white drop-shadow-lg">
                  {color.id === 0 && "ðŸ”´"}
                  {color.id === 1 && "ðŸ”µ"}
                  {color.id === 2 && "ðŸŸ¢"}
                  {color.id === 3 && "ðŸŸ¡"}
                </div>
              </button>
            ))}
          </div>

          {gameState.gameStatus !== "playing" && gameState.gameStatus !== "showing" && (
            <div className="mt-6 text-center">
              {gameState.gameStatus === "waiting" && (
                <>
                  <div className="text-xl mb-2">ðŸŽµ Ready to Play Simon Says?</div>
                  <div className="text-sm text-muted-foreground">Watch the sequence, then repeat it!</div>
                </>
              )}

              {gameState.gameStatus === "gameover" && (
                <>
                  <div className="text-xl mb-2">Game Over! ðŸŽ®</div>
                  <div className="text-sm mb-2">Final Score: {gameState.score}</div>
                  <div className="text-sm mb-2">Rounds Completed: {gameState.round - 1}</div>
                  {gameState.score > bestScore && (
                    <div className="text-xs text-yellow-300 mt-1">New Best Score! ðŸŽ‰</div>
                  )}
                </>
              )}
            </div>
          )}

          {gameState.gameStatus === "showing" && (
            <div className="mt-6 text-center">
              <div className="text-lg mb-2">ðŸ‘€ Watch the Sequence</div>
              <div className="text-sm text-muted-foreground">Round {gameState.round}</div>
            </div>
          )}

          {gameState.gameStatus === "playing" && gameState.currentStep > 0 && (
            <div className="mt-6 text-center">
              <div className="text-lg mb-2">ðŸŽ¯ Your Turn</div>
              <div className="text-sm text-muted-foreground">
                Step {gameState.currentStep + 1} of {gameState.sequence.length}
              </div>
            </div>
          )}
        </CardContent>
      </Card>

      <Card className="theme-transition">
        <CardContent className="p-3.5 flex justify-between items-center">
          <div className="flex gap-3">
            <Badge variant="outline">Score: {gameState.score}</Badge>
            <Badge variant="outline">Round: {gameState.round}</Badge>
            <Badge variant="outline">Best: {bestScore}</Badge>
          </div>
          <div className="flex gap-2">
            <Button 
              variant="outline" 
              size="sm"
              onClick={toggleFullscreen}
              className="px-2"
            >
              {isFullscreen ? <Minimize className="w-4 h-4" /> : <Maximize className="w-4 h-4" />}
            </Button>
            <Button variant="outline" onClick={resetGame}>Reset</Button>
            <Button
              onClick={gameState.gameStatus === "waiting" ? startGame : resetGame}
              className="bg-pocket-red hover:bg-pocket-red-dark text-white"
              disabled={gameState.gameStatus === "showing"}
            >
              {gameState.gameStatus === "playing" || gameState.gameStatus === "showing" ? "Reset" : "Play"}
            </Button>
          </div>
        </CardContent>
      </Card>

      <Card className="theme-transition">
        <CardContent className="p-3.5">
          <h3 className="text-sm font-semibold mb-2">How to Play</h3>
          <ul className="text-xs text-muted-foreground space-y-1">
            <li>â€¢ Watch the sequence of colors that flash</li>
            <li>â€¢ Click the colors in the same order</li>
            <li>â€¢ Each round adds one more color</li>
            <li>â€¢ Earn 1 â­ for every 6 points scored</li>
          </ul>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/SnakeGame.tsx.content:
import { useState, useEffect, useRef, useCallback } from "react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { useToast } from "@/hooks/use-toast";
import { useAuth } from "@/hooks/useAuth";
import { apiRequest } from "@/lib/queryClient";
import { Maximize, Minimize } from "lucide-react";

interface Position {
  x: number;
  y: number;
}

interface GameState {
  snake: Position[];
  food: Position;
  direction: Position;
  score: number;
  gameStatus: "waiting" | "playing" | "gameover";
}

const GRID_SIZE = 20;
const CANVAS_SIZE = 400;

export default function SnakeGame() {
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const [gameState, setGameState] = useState<GameState>({
    snake: [{ x: 10, y: 10 }],
    food: { x: 15, y: 15 },
    direction: { x: 0, y: 0 },
    score: 0,
    gameStatus: "waiting",
  });

  const [bestScore, setBestScore] = useState(() => {
    return parseInt(localStorage.getItem("snake-best-score") || "0", 10);
  });
  const [isFullscreen, setIsFullscreen] = useState(false);
  const gameContainerRef = useRef<HTMLDivElement>(null);

  const { user } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const awardPointsMutation = useMutation({
    mutationFn: async (points: number) => {
      return apiRequest("POST", "/api/user/points", {
        points,
        reason: `Snake game - scored ${gameState.score} points`,
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/auth/user"] });
      toast({
        title: "Points Earned!",
        description: `You earned ${Math.floor(gameState.score / 3)} points!`,
      });
    },
  });

  const generateFood = useCallback((snake: Position[]): Position => {
    const maxPos = CANVAS_SIZE / GRID_SIZE - 1;
    let newFood: Position;
    do {
      newFood = {
        x: Math.floor(Math.random() * maxPos),
        y: Math.floor(Math.random() * maxPos),
      };
    } while (snake.some(segment => segment.x === newFood.x && segment.y === newFood.y));
    return newFood;
  }, []);

  const resetGame = useCallback(() => {
    const initialSnake = [{ x: 10, y: 10 }];
    setGameState({
      snake: initialSnake,
      food: generateFood(initialSnake),
      direction: { x: 0, y: 0 },
      score: 0,
      gameStatus: "waiting",
    });
  }, [generateFood]);

  const gameLoop = useCallback(() => {
    setGameState(prev => {
      if (prev.gameStatus !== "playing") return prev;

      const newSnake = [...prev.snake];
      const head = { ...newSnake[0] };

      head.x += prev.direction.x;
      head.y += prev.direction.y;

      // Check wall collision
      const maxPos = CANVAS_SIZE / GRID_SIZE - 1;
      if (head.x < 0 || head.x > maxPos || head.y < 0 || head.y > maxPos) {
        return { ...prev, gameStatus: "gameover" };
      }

      // Check self collision
      if (newSnake.some(segment => segment.x === head.x && segment.y === head.y)) {
        return { ...prev, gameStatus: "gameover" };
      }

      newSnake.unshift(head);

      let newFood: Position = prev.food;
      let newScore = prev.score;

      // Check food collision
      if (head.x === prev.food.x && head.y === prev.food.y) {
        newScore += 10;
        newFood = generateFood(newSnake);
      } else {
        newSnake.pop();
      }

      return {
        ...prev,
        snake: newSnake,
        food: newFood,
        score: newScore,
      };
    });
  }, [generateFood]);

  const draw = useCallback(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext("2d");
    if (!ctx) return;

    // Clear canvas
    ctx.fillStyle = "#1a1a1a";
    ctx.fillRect(0, 0, canvas.width, canvas.height);

    // Draw snake
    ctx.fillStyle = "#4ade80";
    gameState.snake.forEach((segment, index) => {
      ctx.fillStyle = index === 0 ? "#22c55e" : "#4ade80";
      ctx.fillRect(
        segment.x * GRID_SIZE,
        segment.y * GRID_SIZE,
        GRID_SIZE - 2,
        GRID_SIZE - 2
      );
    });

    // Draw food
    ctx.fillStyle = "#ef4444";
    ctx.fillRect(
      gameState.food.x * GRID_SIZE,
      gameState.food.y * GRID_SIZE,
      GRID_SIZE - 2,
      GRID_SIZE - 2
    );

    // Draw grid
    ctx.strokeStyle = "#333";
    ctx.lineWidth = 1;
    for (let i = 0; i <= CANVAS_SIZE; i += GRID_SIZE) {
      ctx.beginPath();
      ctx.moveTo(i, 0);
      ctx.lineTo(i, CANVAS_SIZE);
      ctx.stroke();
      ctx.beginPath();
      ctx.moveTo(0, i);
      ctx.lineTo(CANVAS_SIZE, i);
      ctx.stroke();
    }
  }, [gameState]);

  useEffect(() => {
    if (gameState.gameStatus === "playing") {
      const interval = setInterval(gameLoop, 150);
      return () => clearInterval(interval);
    }
  }, [gameState.gameStatus, gameLoop]);

  useEffect(() => {
    draw();
  }, [gameState, draw]);

  useEffect(() => {
    if (gameState.gameStatus === "gameover") {
      const newBestScore = Math.max(bestScore, gameState.score);
      if (newBestScore > bestScore) {
        setBestScore(newBestScore);
        localStorage.setItem("snake-best-score", newBestScore.toString());
      }

      const pointsEarned = Math.floor(gameState.score / 3);
      if (pointsEarned > 0) {
        awardPointsMutation.mutate(pointsEarned);
      }
    }
  }, [gameState.gameStatus, gameState.score, bestScore, awardPointsMutation]);

  useEffect(() => {
    const handleKeyPress = (e: KeyboardEvent) => {
      // Only handle if game is playing and focus is on game area
      if (gameState.gameStatus !== "playing") return;

      const keyDirections: { [key: string]: Position } = {
        ArrowUp: { x: 0, y: -1 },
        ArrowDown: { x: 0, y: 1 },
        ArrowLeft: { x: -1, y: 0 },
        ArrowRight: { x: 1, y: 0 },
        w: { x: 0, y: -1 },
        s: { x: 0, y: 1 },
        a: { x: -1, y: 0 },
        d: { x: 1, y: 0 },
        W: { x: 0, y: -1 },
        S: { x: 0, y: 1 },
        A: { x: -1, y: 0 },
        D: { x: 1, y: 0 },
      };

      const newDirection = keyDirections[e.key];
      if (newDirection) {
        e.preventDefault();
        e.stopPropagation();
        setGameState(prev => {
          // Prevent reverse direction
          if (newDirection.x === -prev.direction.x && newDirection.y === -prev.direction.y) {
            return prev;
          }
          return { ...prev, direction: newDirection };
        });
      }
    };

    if (gameState.gameStatus === "playing") {
      document.addEventListener("keydown", handleKeyPress, { capture: true });
      return () => document.removeEventListener("keydown", handleKeyPress, { capture: true });
    }
  }, [gameState.gameStatus, gameState.direction]);

  const startGame = () => {
    setGameState(prev => ({ ...prev, gameStatus: "playing", direction: { x: 1, y: 0 } }));
  };

  const handleDirectionChange = (newDirection: Position) => {
    if (gameState.gameStatus !== "playing") return;

    setGameState(prev => {
      // Prevent reverse direction
      if (newDirection.x === -prev.direction.x && newDirection.y === -prev.direction.y) {
        return prev;
      }
      return { ...prev, direction: newDirection };
    });
  };

  const toggleFullscreen = async () => {
    if (!gameContainerRef.current) return;

    try {
      if (!isFullscreen) {
        if (gameContainerRef.current.requestFullscreen) {
          await gameContainerRef.current.requestFullscreen();
        }
      } else {
        if (document.exitFullscreen) {
          await document.exitFullscreen();
        }
      }
    } catch (error) {
      console.error('Fullscreen error:', error);
    }
  };

  useEffect(() => {
    const handleFullscreenChange = () => {
      setIsFullscreen(!!document.fullscreenElement);
    };

    document.addEventListener('fullscreenchange', handleFullscreenChange);
    return () => document.removeEventListener('fullscreenchange', handleFullscreenChange);
  }, []);

  return (
    <div ref={gameContainerRef} className={`space-y-4 ${isFullscreen ? 'fixed inset-0 z-50 bg-background p-4 overflow-auto' : ''}`}>
      <div className="text-center">
        <div className="relative inline-block">
          <h2 className="text-2xl font-bold mb-2 bg-gradient-to-r from-green-400 to-emerald-600 bg-clip-text text-transparent">ðŸ Snake Game</h2>
          <div className="absolute -inset-1 bg-gradient-to-r from-green-400 to-emerald-600 rounded-lg blur opacity-20 animate-pulse"></div>
        </div>
        <p className="text-sm text-muted-foreground">Eat the red food to grow and become the ultimate snake!</p>
      </div>

